import {useState} from "react"
import './App.css';
import Navbar from "./components/navbar/Navbar";
import UserLisr from "./components/userlist/UserLisr";
import NewUserForm from "./components/newuser/NewUserForm";



function App() {

  const [user, setUser] = useState([]) 
  const [showModal, setShowModal] = useState(false)

const deleteUser = (id)=> {
  setUser((prev) => {
       return prev.filter((value) => value.id !== id 
      )
    })
  }

  const closeModal =(e)=> {
    if(e.target.className === "overlay") setShowModal(false)
  }



  return (
    <div className="App" onClick={closeModal}>
      <Navbar user={user} />
      <main>
        <div className="no-users">
          {user.length === 0 && <h2>Not User</h2>}
        </div>
          <UserLisr user={user} deleteUser={deleteUser} />
         {showModal && <NewUserForm />}
          <button className="create-user" onClick={()=>setShowModal(true)}>Create user</button>
      </main>
    </div>
  );
    }


export default App;

