import React, { useState } from "react";
import Card from "./Card";
import Navbar from "./Navbar";
import RegistrPage from "./RegistrPage";
import './style.css'

function MainPage() {
  const [showModal, setShowModal] = useState(false)
  // const [closeModal, setCloseModal] = useState(false)
  const [users, setUsers] = useState([
    // {
    //   id: 1,
    //   name: "Rashford Marcus",
    //   age: 25,
    //   from: "England",
    //   job: "Web developer",
    //   gender: "male",
    // },
    // {
    //   id: 2,
    //   name: "Jorj",
    //   age: 25,
    //   from: "German",
    //   job: "Full stack",
    //   gender: "male",
    // },
    // {
    //   id: 3,
    //   name: "Joe Bayden",
    //   age: 25,
    //   from: "French",
    //   job: "Web design",
    //   gender: "Female",
    // },
  ]);

  const newUser =(e)=> {
    setUsers((prev) => {
      return [...prev, e]
    })
  }

  const openModal =()=> {
    setShowModal(true)
  }

  const handleDelete = (id) => {
    let newUsers = users.filter((value) => value.id !== id);
    setUsers(newUsers);
  };
  return (
    <>
      <Navbar users={users} />
      <button className="create-btn" onClick={()=>openModal(true)}>Create user</button>
      <Card handleDelete={handleDelete} users={users} />
      { showModal && <RegistrPage setShowModal={setShowModal} newUser={newUser} />}
    </>
  );
}

export default MainPage;
