import {useState} from "react"
import './App.css';

function App() {

const [showContent, setShowContent] = useState(true)
  const [select, setSelect] = useState([
    {id: 1, title: 'apple'},
    {id: 2, title: 'banana'},
    {id: 3, title: 'orange'},
    {id: 4, title: 'kiwi'}
  ])

const target = () => {
  setShowContent !== showContent
}

  const handleDelete = (id) => {
    let newSelect = select.filter((value) => value.id !== id)
    setSelect(newSelect)
  }

  return (
    <div className="App">
      <button onClick={target}>Hidden Button</button>
      {
        showContent && select.map((value) => {
          return(
            <div key={value.id}>
              <h1>{value.title}</h1>
              <button onClick={() => (handleDelete(value.id))}>delete</button>
            </div>
          )
        })
      }
    </div>
  );
}

export default App;
