import {useState} from "react"
import './App.css';
import EventList from "./components/EventList";
import Modal from "./components/Modal";
import NewEventForm from "./components/NewEventForm";
import Title from "./components/Title";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Toast } from "react-toastify/dist/components";

function App() {

const [showContent, setShowContent] = useState(true)
const [showModal, setShowModal] = useState(true)
const [events, setEvents] = useState([])


  //Add events
  const newEvent =(e)=> {
    if(events.length === 0) {
      toast.error("Iltimos barcha maydonni to'ldiring")
    } else {
      setEvents((prev) => {
      return [...prev, e]
    })
    setShowModal(false)
    }

  }
 //Delete Events
  const handleDelete = (id) => {
    let newSelect = events.filter((value) => value.id !== id)
    setEvents(newSelect)
  }

//Title value
  let Titlee = "Web developer"
  let piteg = "Lorem ipsum dolor sit amet consectetur, adipisicing elit. Minima, quos!"

  return (
    <div className="App">
      <ToastContainer />
      <Title  Titlee={Titlee} piteg={piteg}/>
      { showContent ? <button onClick={() => {setShowContent(false)}}>Hidden Button</button> : <button onClick={() => {setShowContent(true)}}>Show Button</button>}
      {showContent && <EventList handleDelete={handleDelete} events={events} />
      }
      {showModal && <Modal>
        <NewEventForm newEvent={newEvent} />
      </Modal>}
      <button onClick={()=>setShowModal(true)}>Add Event</button>
    </div>
  );
    }


export default App;
