import {useState} from "react"
import './App.css';

function App() {

  const [select, setSelect] = useState([
    {id: 1, title: 'apple'},
    {id: 2, title: 'banana'},
    {id: 3, title: 'orange'},
    {id: 4, title: 'kiwi'}
  ])

  const handleDelete = (id) => {
    let newSelect = select.filter((value) => {
      return value.id !== id
    } )
    setSelect(newSelect)
  }

  return (
    <div className="App">
      {
        select.map((value, index) => {
          return(
            <>
              <h1>{value.title}</h1>
              <button onClick={() => (handleDelete(value.id))}>delete</button>
            </>
          )
        })
      }
    </div>
  );
}

export default App;
