import {useState} from "react"
import './App.css';
import Title from "./components/Title";

function App() {

const [showContent, setShowContent] = useState(true)
const [info, setINfo] = useState(true)
  const [select, setSelect] = useState([
    {id: 1, title: 'apple'},
    {id: 2, title: 'banana'},
    {id: 3, title: 'orange'},
    {id: 4, title: 'kiwi'}
  ])



  const handleDelete = (id) => {
    let newSelect = select.filter((value) => value.id !== id)
    setSelect(newSelect)
  }

  let Title = "Web developer"
  let piteg = "Lorem ipsum dolor sit amet consectetur, adipisicing elit. Minima, quos!"

  return (
    <div className="App">
      <Title  Title={Title} piteg={piteg}/>
      { showContent ? <button onClick={() => {setShowContent(false)}}>Hidden Button</button> : <button onClick={() => {setShowContent(true)}}>Show Button</button>}


      {showContent && <div>
        {select.length === 0 && <h1>Not content</h1>}
        {select.map((value) => {
          return(
            <div className="app-info" key={value.id}>
              <h1>{value.title}</h1>
              <button onClick={() => (handleDelete(value.id))}>delete</button>
            </div>
          )
        }) }
      </div> 
      }
    </div>
  );
    }


export default App;
