import {useState} from "react"
import './App.css';
import EventList from "./components/EventList";
import Modal from "./components/Modal";
import NewEventForm from "./components/NewEventForm";


function App() {

const [showContent, setShowContent] = useState(true)
const [showModal, setShowModal] = useState(true)
const [events, setEvents] = useState([])


  //Add events
  const newEvent =(e)=> {
      setEvents((prev) => {
      return [...prev, e]
    })
    setShowModal(false)
    }


 //Delete Events
  const handleDelete = (id) => {
    let newSelect = events.filter((value) => value.id !== id)
    setEvents(newSelect)
  }


  return (
    <div className="App">
      { showContent ? <button onClick={() => {setShowContent(false)}}>Hidden Button</button> : <button onClick={() => {setShowContent(true)}}>Show Button</button>}
      {showContent && <EventList handleDelete={handleDelete} events={events} />
      }
      {showModal && <Modal>
        <NewEventForm newEvent={newEvent} />
      </Modal>}
      <button onClick={()=>setShowModal(true)}>Add Event</button>
    </div>
  );
    }


export default App;
